            $("li." + bl_name + "_blogs_hidden").each(function (i) {
                $(this).css("display", "none");
                $(this).attr("class", "");
                $("#" + bl_name + "_show-all").css("display", "block");
                $("#" + bl_name + "_show-n").css("display", "none");
            });
        });
    });
